$(document).ready(function () {
    // üõí A√±adir a la lista de cotizaci√≥n
    $(document).on("click", "#a√±adirLista", function () {
        let nombre = $("#nombre").text();
        let descripcion = $("#descripcion").text();
        let precio = $("#precio").text();
        let cantidad = $("#cantidad").val();

        // Obtener la lista actual desde localStorage
        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];

        // Verificar si el producto ya est√° en la lista
        if (!listaProductos.some(p => p.nombre === nombre)) {
            listaProductos.push({ nombre, descripcion, precio, cantidad });

            // Guardar la lista actualizada en localStorage
            localStorage.setItem("listaCotizacion", JSON.stringify(listaProductos));

            $('#alerta').fadeIn();  // Muestra la alerta de manera gradual


            setTimeout(function () {
                $('#alerta').fadeOut();
            }, 3000);

        } else {

            $('#alerta2').fadeIn();  // Muestra la alerta de manera gradual

            // Despu√©s de 3 segundos, desaparecer√° (puedes ajustar el tiempo si lo deseas)
            setTimeout(function () {
                $('#alerta2').fadeOut();  // Desvanece la alerta de manera gradual
            }, 3000);  // 3000 ms = 3 segundos
        }

        // Verificamos que estamos en un iframe y si el iframe puede acceder al DOM del padre
        if (window.parent && window.parent.document) {
            // Usamos jQuery para acceder al bot√≥n fuera del iframe
            var boton = $(window.parent.document).find('#verLista');  // Seleccionamos el bot√≥n por ID

            // Aseguramos que el bot√≥n tenga un posicionamiento relativo
            boton.css('position', 'relative');

            // Creamos el span con las clases necesarias
            var span = $('<span></span>', {
                class: 'position-absolute top-0 start-100 translate-middle p-2 bg-danger border border-light rounded-circle'
            });

            // A√±adimos el span al bot√≥n
            boton.append(span);
        }


    });

    // üìã Mostrar la lista en el modal
    $(document).on("click", "#verLista", function () {
        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];
        let listaHTML = "";

        if (listaProductos.length === 0) {
            listaHTML = `<li class="list-group-item text-center">No hay productos en la lista.</li>`;
        } else {
            listaProductos.forEach((p, index) => {
                listaHTML += `<li class="list-group-item d-flex justify-content-between align-items-center">
                                <div>
                                    <strong>${p.nombre}</strong> - ${p.descripcion}
                                    <br><span class="text-success">${p.precio}</span>
                                    <br><span class="text-muted">Cantidad: ${p.cantidad}</span>
                                </div>
                                <button class="btn btn-danger btn-sm btn-remove" data-index="${index}">X</button>
                              </li>`;
            });
        }

        $("#listaCotizacion").html(listaHTML);
        $("#listaModal").modal("show");


        var boton = $(window.parent.document).find('#verLista');


        boton.css('position', 'relative');


        var span = $('<span></span>', {
            class: 'position-absolute top-0 start-100 translate-middle p-2 bg-danger border border-light rounded-circle'
        });


        boton.append(span);

        boton.find('span').remove();

    });

    // ‚ùå Eliminar productos de la lista
    $(document).on("click", ".btn-remove", function () {
        let index = $(this).data("index");
        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];
        listaProductos.splice(index, 1);
        localStorage.setItem("listaCotizacion", JSON.stringify(listaProductos));
        $("#verLista").trigger("click"); // Recargar la lista
    });

    // ‚úÖ Bot√≥n "Cotizar ahora" (esto puede enviar la lista a otra p√°gina o un backend)
    $(document).on("click", "#cotizarAhora", function () {

        $("#listaModal").modal("hide");

        cargarCotizacion();


    });

    $(document).on("click", "#vaciarLista", function () {
        Swal.fire({
            title: "¬øVaciar lista?",
            text: "Esta acci√≥n eliminar√° todos los productos de la cotizaci√≥n.",
            icon: "warning",
            showCancelButton: true,
            confirmButtonText: "S√≠, vaciar",
            cancelButtonText: "Cancelar",
            confirmButtonColor: "#d33",
            cancelButtonColor: "#3085d6"
        }).then((result) => {
            if (result.isConfirmed) {
                localStorage.removeItem("listaCotizacion");
                $("#verLista").trigger("click");
                Swal.fire("Lista vaciada", "Tu cotizaci√≥n ha sido eliminada.", "success");
            }
        });
    });
    



    if ($("#cotizaciones").length) {
        cargarCotizacion(); // Solo se ejecuta en la p√°gina de cotizaci√≥n
    }

    // Funci√≥n para cargar la cotizaci√≥n
    function cargarCotizacion() {

        var iframe = $("#product-frame");
        iframe.attr("src", "cotizacion.php");

        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];
        let listaHTML = "";
        let totalSinIVA = 0;

        if (listaProductos.length === 0) {
            listaHTML = `<tr><td colspan="4" class="text-center">No hay productos en la lista.</td></tr>`;
        } else {
            listaProductos.forEach(p => {
                let precio = parseFloat(p.precio.replace(/\$/g, ""));
                let cantidad = parseInt(p.cantidad);

                let precioTotal = precio * cantidad;
                totalSinIVA += precioTotal;

                listaHTML += `<tr>
                            <td>${p.nombre}</td>
                            <td>${p.precio}</td>
                            <td>${p.cantidad}</td>
                            <td>${precioTotal.toFixed(2)}</td>
                          </tr>`;
            });
        }

        // Agregar las filas a la tabla
        $("#tablaCotizacion").html(listaHTML);

        // Calcular el total con IVA (15%)
        let iva = totalSinIVA * 0.15;
        let totalConIVA = totalSinIVA + iva;

        // Mostrar los totales en la p√°gina
        $("#totalSinIVA").text(totalSinIVA.toFixed(2));
        $("#totalConIVA").text(totalConIVA.toFixed(2));
        
        
    }

    $(document).on("click", "#descargarPDF", function () {
        const { jsPDF } = window.jspdf;
        let doc = new jsPDF();
        let logo = "data:image/png;base64,";

        let titulo = "Cotizaci√≥n";
        let nombreCat = "FireSafe - Cat√°logo de seguridad contra incendios"
        // Agregar el logo
        doc.addImage(logo, "PNG", 10, 10, 30, 30); // (imagen, formato, x, y, ancho, alto)
    
        // T√≠tulo de la cotizaci√≥n
        doc.setFontSize(18);
        doc.text(titulo, 80, 20);
        doc.text(nombreCat, 50, 30);
    
        // Obtener los productos de la cotizaci√≥n
        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];
        let datosTabla = listaProductos.map((p) => [
            p.nombre,
            p.descripcion,
            p.cantidad,
            p.precio,
            (parseFloat(p.precio.replace(/[^\d.]/g, "")) * parseInt(p.cantidad)).toFixed(2),
        ]);
    
        // Calcular los totales
        let totalSinIVA = datosTabla.reduce((sum, p) => sum + parseFloat(p[4]), 0);
        let totalConIVA = (totalSinIVA * 1.15).toFixed(2);
    
        // Generar la tabla
        doc.autoTable({
            head: [["Producto", "Descripci√≥n", "Cantidad", "Precio Unitario", "Total"]],
            body: datosTabla,
            startY: 50,
        });
    
        // Agregar los totales
        doc.setFontSize(14);
        doc.text(`Total sin IVA: $${totalSinIVA.toFixed(2)}`, 120, doc.autoTable.previous.finalY + 10);
        doc.text(`Total con IVA (15%): $${totalConIVA}`, 120, doc.autoTable.previous.finalY + 20);
    
        // Descargar el PDF
        doc.save("cotizacion.pdf");

        
    });
   
    
    $(document).on("click", "#descargarExcel", function () {
        let wb = XLSX.utils.book_new();
        let titulo = "Cotizaci√≥n";
        let listaProductos = JSON.parse(localStorage.getItem("listaCotizacion")) || [];
    
        // Crear la tabla
        let datosTabla = [["Producto", "Descripci√≥n", "Cantidad", "Precio Unitario", "Total"]];
        let totalSinIVA = 0;
    
        listaProductos.forEach((p) => {
            let precio = parseFloat(p.precio.replace(/[^\d.]/g, ""));
            let cantidad = parseInt(p.cantidad);
            let total = precio * cantidad;
            totalSinIVA += total;
            datosTabla.push([p.nombre, p.descripcion, cantidad, `$${precio.toFixed(2)}`, `$${total.toFixed(2)}`]);
        });
    
        // Agregar totales
        let totalConIVA = (totalSinIVA * 1.15).toFixed(2);
        datosTabla.push([]);
        datosTabla.push(["", "", "", "Total sin IVA:", `$${totalSinIVA.toFixed(2)}`]);
        datosTabla.push(["", "", "", "Total con IVA (15%):", `$${totalConIVA}`]);
    
        // Convertir a hoja de Excel
        let ws = XLSX.utils.aoa_to_sheet(datosTabla);
        XLSX.utils.book_append_sheet(wb, ws, "Cotizaci√≥n");
    
        // Descargar el archivo
        XLSX.writeFile(wb, "cotizacion.xlsx");
    });
    


});